import React from "react";
import NumberCell from "./NumberCell";
import "./Board.css";

export default function Board({ board, onCellClick, selectedCells, isCellSelected, shakeBoard, removedCells, successCells, newNumbers, emptyCells, isExpanding }) {
  // board가 없거나 비어있으면 로딩 표시
  if (!board || !Array.isArray(board) || board.length === 0) {
    return (
      <div className="board-container">
        <div className="loading">Loading board...</div>
      </div>
    );
  }

  // Combine animation classes
  const boardClass = `board-container ${shakeBoard ? 'shake' : ''} ${isExpanding ? 'expanding' : ''}`;

  return (
    <div className={boardClass}>
      <table className="game-board">
        <tbody>
          {board.map((row, i) => (
            <tr key={i}>
              {row.map((num, j) => {
                const cellKey = `${i},${j}`;
                const isRemoved = removedCells.includes(cellKey);
                const isSuccess = successCells.includes(cellKey);
                const isNewNumber = newNumbers && newNumbers.includes(cellKey);
                const isEmptyCell = emptyCells && emptyCells.includes(cellKey);

                return (
                  <NumberCell 
                    key={j} 
                    value={num} 
                    row={i} 
                    col={j}
                    onClick={onCellClick}
                    isSelected={isCellSelected(i, j)}
                    isRemoved={isRemoved}
                    isSuccess={isSuccess}
                    isNewNumber={isNewNumber}
                    isEmptyCell={isEmptyCell}
                  />
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
