import React, { useEffect, useState } from "react";
import { startGame, removeNumbers, addNumbers } from "./api/gameApi";
import Board from "./components/Board";
import Controls from "./components/Controls";
import soundEffects from "./utils/soundEffects";
import "./App.css";

// Simple modal popup for game instructions
function HowToPlayModal({ open, onClose }) {
  if (!open) return null;
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <h2>How to Play</h2>
        <ul>
          <li>At the start, 4 of each number from 1 to 9 are randomly placed in a 4x9 board (36 numbers).</li>
          <li>You can remove two numbers if they are <b>adjacent</b> (including diagonals), or if there are no numbers between them (even across rows).</li>
          <li>The two numbers must be <b>the same</b> or <b>sum to 10</b>.</li>
          <li>You can add a new set of 36 numbers up to 5 times using the Add Numbers button.</li>
          <li>Click two numbers to try to remove them. If not removable, your selection will be cleared.</li>
        </ul>
        <button className="close-modal" onClick={onClose}>Close</button>
      </div>
    </div>
  );
}

// Main React component for the Number Game
export default function App() {
  // game: stores the current game state (board, remaining adds)
  const [game, setGame] = useState(null);
  // selectedCells: stores which cells the user has currently selected
  const [selectedCells, setSelectedCells] = useState([]);
  // isLoading: true while the game is being initialized
  const [isLoading, setIsLoading] = useState(true);
  // showHowTo: controls visibility of the How to Play modal
  const [showHowTo, setShowHowTo] = useState(false);
  // animation states
  const [shakeBoard, setShakeBoard] = useState(false);
  const [removedCells, setRemovedCells] = useState([]);
  const [successCells, setSuccessCells] = useState([]);
  const [newNumbers, setNewNumbers] = useState([]);
  const [emptyCells, setEmptyCells] = useState([]);
  const [isExpanding, setIsExpanding] = useState(false);
  // sound on/off state
  const [soundOn, setSoundOn] = useState(true);

  // Toggle sound
  const toggleSound = () => {
    const newSoundOn = !soundOn;
    setSoundOn(newSoundOn);
    soundEffects.setMuted(!newSoundOn);
  };

  // Function to start a new game (used for both initial load and 'New Game' button)
  const startNewGame = () => {
    // Resume audio context on first interaction
    soundEffects.resume();
    soundEffects.click();

    setIsLoading(true);
    setSelectedCells([]);
    startGame().then((data) => {
      setGame(data);
      setIsLoading(false);
    }).catch((error) => {
      console.error("Failed to start game:", error);
      setIsLoading(false);
    });
  };

  // useEffect runs once when the component mounts (like componentDidMount)
  useEffect(() => {
    startNewGame(); // Start a new game on first load
  }, []); // empty dependency array: only run once

  // Handles when a user clicks a cell on the board
  const handleCellClick = (row, col) => {
    // Validate board and indices to prevent errors
    if (!game || !game.board || 
        row < 0 || row >= game.board.length || 
        col < 0 || col >= game.board[row].length ||
        game.board[row][col] === null) return;

    // Resume audio context on first interaction
    soundEffects.resume();

    // Create a unique key for the cell
    const cellKey = `${row},${col}`;
    // Check if this cell is already selected
    const isAlreadySelected = selectedCells.some(cell => cell.key === cellKey);

    if (isAlreadySelected) {
      // If already selected, unselect it
      soundEffects.click();
      setSelectedCells(selectedCells.filter(cell => cell.key !== cellKey));
    } else {
      // Play pop sound for selection
      soundEffects.pop();

      // Otherwise, add it to the selection
      const newSelected = [...selectedCells, { row, col, key: cellKey, value: game.board[row][col] }];

      if (newSelected.length === 2) {
        // If two cells are selected, try to remove them
        const [cell1, cell2] = newSelected;
        if (canRemove(cell1, cell2)) {
          // Play success sound
          soundEffects.success();

          // Add success animation for selected cells
          setSuccessCells([cell1.key, cell2.key]);
          setTimeout(() => setSuccessCells([]), 500);

          handleRemove([cell1.row, cell1.col], [cell2.row, cell2.col]);
          setSelectedCells([]); // Clear selection after attempt
        } else {
          // Play error sound
          soundEffects.error();

          // If not removable, trigger shake animation
          setShakeBoard(true);
          setTimeout(() => setShakeBoard(false), 500);
          setSelectedCells([]);
        }
      } else {
        setSelectedCells(newSelected);
      }
    }
  };

  // Checks if two selected cells can be removed (same number or sum to 10)
  const canRemove = (cell1, cell2) => {
    return cell1.value === cell2.value || cell1.value + cell2.value === 10;
  };

  // Handles the actual removal by calling the backend
  const handleRemove = (pos1, pos2) => {
    // Add fade out animation for cells being removed
    const cell1Key = `${pos1[0]},${pos1[1]}`;
    const cell2Key = `${pos2[0]},${pos2[1]}`;
    setRemovedCells([cell1Key, cell2Key]);

    removeNumbers(game, pos1, pos2)
      .then((data) => {
        // Play remove sound
        soundEffects.remove();

        setGame(data); // Update the board with the new state
        // Clear removed cells animation after a short delay
        setTimeout(() => setRemovedCells([]), 300);
        // Check if game is won
        if (data.game_won) {
          // Play win sound
          soundEffects.win();
          setTimeout(() => alert("Game Win! ðŸŽ‰"), 800); // Delay alert to let win sound play
        }
      })
      .catch(async (error) => {
        // If error, show a message and clear selection
        let message = "ì œê±°í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.";
        if (error && error.response) {
          try {
            const errJson = await error.response.json();
            if (errJson && errJson.detail) message = errJson.detail;
          } catch {}
        }
        alert(message);
        setSelectedCells([]); // Only clear selection, don't change the board
        setRemovedCells([]); // Clear removed cells animation
      });
  };

  // Handles the "Add Numbers" button (adds a new set of numbers to the board)
  const handleAdd = () => {
    // Resume audio context on first interaction
    soundEffects.resume();
    soundEffects.click();

    // Request animation states from backend
    addNumbers(game, true).then((data) => {
      if (data.animation && data.states) {
        // Handle animation states
        animateAddNumbers(data.states);
      } else {
        // Fallback to immediate update (no animation)
        setGame(data);
      }
    }).catch((error) => {
      console.error("Failed to add numbers:", error);
      // Fallback to non-animated version
      addNumbers(game, false).then((data) => {
        setGame(data);
      });
    });
  };

  // Animate the placement of numbers step by step
  const animateAddNumbers = (animationStates) => {
    let currentStep = 0;
    let animationTimeout = null;

    // Set a timeout to fallback to immediate add if animation takes too long
    const fallbackTimeout = setTimeout(() => {
      console.log("Animation timeout, falling back to immediate add");
      addNumbers(game, false).then((data) => {
        setGame(data);
        setNewNumbers([]);
        setEmptyCells([]);
        setIsExpanding(false);
      });
    }, 10000); // 10 second timeout

    const animateStep = () => {
      if (currentStep >= animationStates.length) {
        // Clear timeout and animation state when complete
        clearTimeout(fallbackTimeout);
        setNewNumbers([]);
        setEmptyCells([]);
        setIsExpanding(false);
        return; // Animation complete
      }

      const step = animationStates[currentStep];

      if (step.step === "empty_board") {
        // Show empty board with new rows
        setGame(step.state);
        setIsExpanding(true);

        // Mark all new empty cells for fade-in animation
        const newEmptyCells = [];
        const originalRowCount = game.board.length;
        for (let i = originalRowCount; i < step.state.board.length; i++) {
          for (let j = 0; j < step.state.board[i].length; j++) {
            newEmptyCells.push(`${i},${j}`);
          }
        }
        setEmptyCells(newEmptyCells);

        currentStep++;
        animationTimeout = setTimeout(animateStep, 500); // Wait 500ms before starting to place numbers
      } else if (step.step === "place_number") {
        // Place number with animation
        setGame(step.state);
        setIsExpanding(false); // Stop expanding animation when placing numbers

        // Mark the newly placed number for drop-in animation
        const newNumberKey = `${step.position[0]},${step.position[1]}`;
        setNewNumbers([newNumberKey]);

        // Play sound for number placement
        soundEffects.pop();

        currentStep++;
        // Faster animation for number placement (100ms between each number)
        animationTimeout = setTimeout(animateStep, 100);
      } else if (step.step === "complete") {
        // Final state - ensure this is the complete state with all numbers
        clearTimeout(fallbackTimeout);
        setGame(step.state);
        // Clear animation state
        setNewNumbers([]);
        setEmptyCells([]);
        setIsExpanding(false);
        // Play completion sound
        soundEffects.success();

        // Verify that all numbers are properly placed and available
        console.log("Animation complete. Final board state:", step.state.board);
      }
    };

    // Start animation
    animateStep();

    // Return cleanup function
    return () => {
      clearTimeout(fallbackTimeout);
      if (animationTimeout) {
        clearTimeout(animationTimeout);
      }
    };
  };

  // Helper to check if a cell is currently selected
  const isCellSelected = (row, col) => {
    return selectedCells.some(cell => cell.row === row && cell.col === col);
  };

  // Show loading message while initializing
  if (isLoading) return <div className="loading">Loading...</div>;

  // Main render: title, controls row (new game left, add right), and board
  return (
    <div className="app">
      {/* Sound toggle button at top right, left of How to Play */}
      <button
        className={`sound-toggle-btn${soundOn ? "" : " sound-off"}`}
        onClick={toggleSound}
        title={soundOn ? "íš¨ê³¼ìŒ ë„ê¸°" : "íš¨ê³¼ìŒ ì¼œê¸°"}
        aria-label={soundOn ? "íš¨ê³¼ìŒ ë„ê¸°" : "íš¨ê³¼ìŒ ì¼œê¸°"}
      >
        {soundOn ? "ðŸ”Š" : "ðŸ”‡"}
      </button>
      {/* How to Play button at top right */}
      <button 
        className="howto-btn" 
        onClick={() => {
          soundEffects.resume();
          soundEffects.click();
          setShowHowTo(true);
        }} 
        title="How to Play"
      >
        ?
      </button>
      <HowToPlayModal open={showHowTo} onClose={() => setShowHowTo(false)} />
      <h1 className="game-title">Number Game</h1>
      <div className="controls-row">
        <button className="new-game-button" onClick={startNewGame}>New Game</button>
        {game && <Controls remaining={game.remaining_adds} onAdd={handleAdd} />}
      </div>
      <Board 
        board={game?.board || []} 
        onCellClick={handleCellClick}
        selectedCells={selectedCells}
        isCellSelected={isCellSelected}
        shakeBoard={shakeBoard}
        removedCells={removedCells}
        successCells={successCells}
        newNumbers={newNumbers}
        emptyCells={emptyCells}
        isExpanding={isExpanding}
      />
    </div>
  );
}
