import React from "react";
import NumberCell from "./NumberCell";
import "./Board.css";

export default function Board({ board, onCellClick, selectedCells, isCellSelected }) {
  // board가 없으면 로딩 표시
  if (!board || !Array.isArray(board)) {
    return (
      <div className="board-container">
        <div className="loading">Loading board...</div>
      </div>
    );
  }

  // TODO: 셀 선택/제거 로직 구현
  return (
    <div className="board-container">
      <table className="game-board">
        <tbody>
          {board.map((row, i) => (
            <tr key={i}>
              {row.map((num, j) => (
                <NumberCell 
                  key={j} 
                  value={num} 
                  row={i} 
                  col={j}
                  onClick={onCellClick}
                  isSelected={isCellSelected(i, j)}
                />
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
