import React from "react";
import "./Controls.css";

export default function Controls({ remaining, onAdd, isAnimating }) {
  return (
    <div className="controls">
      <button 
        className={`add-button ${isAnimating ? 'animating' : ''}`}
        onClick={onAdd} 
        disabled={remaining <= 0 || isAnimating}
      >
        {isAnimating ? 'Adding Numbers...' : `Add Numbers (${remaining} left)`}
      </button>
    </div>
  );
}
