import React, { useEffect, useState } from "react";
import { startGame, removeNumbers, addNumbers } from "./api/gameApi";
import Board from "./components/Board";
import Controls from "./components/Controls";
import "./App.css";

function saveToLocal(state) {
  localStorage.setItem("numberGameState", JSON.stringify(state));
}

function loadFromLocal() {
  const saved = localStorage.getItem("numberGameState");
  return saved ? JSON.parse(saved) : null;
}

export default function App() {
  const [game, setGame] = useState(null);

  useEffect(() => {
    const saved = loadFromLocal();
    if (saved) {
      setGame(saved);
    } else {
      startGame().then((data) => {
        setGame(data);
        saveToLocal(data);
      });
    }
  }, []);

  const handleRemove = (pos1, pos2) => {
    removeNumbers(game, pos1, pos2).then((data) => {
      setGame(data);
      saveToLocal(data);
    });
  };

  const handleAdd = () => {
    addNumbers(game).then((data) => {
      setGame(data);
      saveToLocal(data);
    });
  };

  if (!game) return <div className="loading">Loading...</div>;

  return (
    <div className="app">
      <h1 className="game-title">Number Game</h1>
      <Board board={game.board} onRemove={handleRemove} />
      <Controls remaining={game.remaining_adds} onAdd={handleAdd} />
    </div>
  );
}
